
function JSBeep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

var Scorer = {

  setup: function() {
    // only reset if needed
    if(!this.ready) {
      this.ready = true;
      this.runs = [];
      document.getElementById("results").value = "moves, capsules, timePerLife, time, ghostsEaten, levelsFinished, score\n";

      this.runCount = 20;
      this.runAlgoIndex = 0;
      this.runHeuristicIndex = 0;

    }
  },

  // checks whether we should run again, and reconfigures what we are testing
  checkToRunAgain: function() {
    if(this.runs.length < this.runCount) {
      return true;
    }

    // start beeping, done testing
    setInterval(function() {
      JSBeep();
    },1000);

    return false;
  },

  startRun: function() {
    var i = this.runs.length;

    // setup initial run
    this.runs[i] = {};
    this.runs[i].moves = 0;
    this.runs[i].capsulesEaten = 0;
    this.runs[i].timePerLife = 0;
    this.runs[i].time = 0;
    this.runs[i].ghostsEaten = 0;
    this.runs[i].levelsFinished = 0;
    this.runs[i].score = 0;

    // setup timings
    this.tLife = new Date();
    this.tGame = new Date();

  },

  stopRun: function() {
    // record these results
    var x = this.runs.length-1;
    var e = "";
    e += this.runs[x].moves + ", ";
    e += this.runs[x].capsulesEaten + ", ";
    e += (this.runs[x].timePerLife/3.0) + ", ";
    e += this.runs[x].time + ", ";
    e += this.runs[x].ghostsEaten + ", ";
    e += this.runs[x].levelsFinished + ", ";
    e += this.runs[x].score + "\n";
    document.getElementById("results").value += e;

  },

  /*
  print: function() {
    var elm = document.getElementById("results");
    var e = "moves, capsules, timePerLife, time, ghostsEaten, levelsFinished, score\n";

    for(var x = 0; x < this.runs.length; x++) {
      e += this.runs[x].moves + ", ";
      e += this.runs[x].capsulesEaten + ", ";
      e += this.runs[x].timePerLife + ", ";
      e += this.runs[x].time + ", ";
      e += this.runs[x].ghostsEaten + ", ";
      e += this.runs[x].levelsFinished + "\n";
    }
    elm.value = e;
    console.info("Printed out data and done");
  },
  */

  addMove: function() {
    this.runs[this.runs.length-1].moves++;
  },

  addCapsuleEaten: function() {
    this.runs[this.runs.length-1].capsulesEaten++;
  },

  recordLifeTime: function() {
    var t = new Date();
    var ms = t - this.tLife;
    this.runs[this.runs.length-1].timePerLife += ms;
  },

  recordGameTime: function() {
    var t = new Date();
    var ms = t - this.tGame;
    this.runs[this.runs.length-1].time = ms;
  },

  addGhostEaten: function() {
    this.runs[this.runs.length-1].ghostsEaten++;
  },

  addLevelFinished: function() {
    this.runs[this.runs.length-1].levelsFinished++;
  },

  addScore: function(y) {
    this.runs[this.runs.length-1].score+=y;
  }

};
